import React, {isValidElement, useState} from 'react'

const NewEventModal = (props) => {

  const {onSave, onClose} = props

  console.log(props)

    const [title, setTitle] = useState('');
    const [reminder, setReminder] = useState('');
    const [location, setLocation] = useState('')

    const [titleError, setTitleError] = useState(false);
    const [titleErrorMsg, setTitleErrorMsg] = useState('')

    const [reminderError, setReminderError] = useState(false);
    const [reminderErrorMsg, setReminderErrorMsg] = useState('')

    const [locationError, setLocationError] = useState(false);
    const [locationErrorMsg, setLocationErrorMsg] = useState('')

    const handleValidation = () => {
      let valid = true;

      if (title.length <= 0) {
        valid = false
        setTitleError(true)
        setTitleErrorMsg('Please add a title')
      }
      if (reminderError.length <= 0) {
        valid = false
        setReminderError(true)
        setReminderErrorMsg('Please add a reminder')
      }
      if (reminderError.length > 30) {
        valid = false
        setReminderError(true)
        setReminderErrorMsg(('Description needs to be shorter than 30 characters'))
      }

      if (locationError.length < 0) {
        valid = false
        setLocationError(true)
        setLocationErrorMsg('Please add a valid city or location')
      }

      return valid
    }

    return(
      <>
        <div id="newEventModal">
          <h2>New Event</h2>
          <input 
            className={titleError ? 'error' : ''}
            value={title} 
            onChange={e => setTitle(e.target.value)} 
            id="eventTitleInput" 
            placeholder="Event Title" 
          />
          <div>{titleErrorMsg}</div>

          <h2>Reminder</h2>

            <input 
              className={reminderError ? 'error' : ''}
              value={reminder} 
              onChange={e => 
                setReminder(e.target.value)} 
              id="eventTitleInput" 
              placeholder="Description Max. 30 chars" 
            />
            <h2 className="charachters-left">Characters left:</h2>
            <div>{reminderErrorMsg}</div>

          <h2>Location/City</h2>
            <input 
              className={locationError ? 'error' : ''}
              value={location} 
              onChange={e => setLocation(e.target.value)} 
              id="eventTitleInput" 
              placeholder="Example: London" 
            />

          <div>{locationErrorMsg}</div>

          <button 
            onClick={ async () => {
              const isFormValid = await handleValidation()
              console.log(isFormValid, 'Loging this')
              if (isFormValid) {
                onSave(title,reminder,location);
              } 
            }} 

            id="saveButton">Save</button>


          <button 
            onClick={onClose}
            id="cancelButton">Cancel</button>
        </div>

        <div id="modalBackDrop"></div>
      </>
    );
  };

export default NewEventModal
