import React, { useState, useEffect } from 'react';
import '../sass/calendar.scss'
import Day from '../components/Day'
import CalendarHeader from '../components/CalendarHeader';

function Calendar(props) {

  //Initalizing States used in Calendar
  const [nav, setNav] = useState(0)
  const [days, setDays] = useState([]);
  const [dateDisplay, setdateDisplay] = useState('');
  const [clicked, setClicked] = useState();

  //Checking if we already have some events stored in local storage.
  const [events, setEvents] = useState(
    localStorage.getItem('events') 
    ? JSON.parse(localStorage.getItem('events')) 
    : []
  );

  const eventForDate = date => events.find(e => e.date === date);

  //UseEffect function that updates state of events each time event changes
  useEffect(() => {
    localStorage.setItem('events', JSON.stringify(events));
  }, [events])

  useEffect(() => {
    const weekdays = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    const dt = new Date();

    if (nav !==0) {
      dt.setMonth(new Date().getMonth() + nav);
    }

    const day = dt.getDate();
    const month = dt.getMonth();
    const year = dt.getFullYear();

    const firstDayOfMonth = new Date(year, month, 1);
    const daysInMonth = new Date(year, month + 1, 0).getDate();

    const dateString = firstDayOfMonth.toLocaleDateString('en-us', {
      weekday: 'long',
      year: 'numeric',
      month: 'numeric',
      day: 'numeric'
    });
    setdateDisplay( `${dt.toLocaleDateString('en-us', { month: 'long' })} ${year}`);

    const paddingDays = weekdays.indexOf(dateString.split(', ')[0]);

    const daysArr = [];

    for (let i = 0; i <= paddingDays + daysInMonth; i++) {
      const dayString = `${month + 1}/${i - paddingDays}/${year}`

      if (i > paddingDays) {
        daysArr.push({
          value: i - paddingDays,
          event: eventForDate(dayString),
          isCurrentDay: i - paddingDays === day && nav === 0  ? true : false,
          date: dayString
        });
      } else {
        daysArr.push({
          value: 'padding',
          event: null,
          isCurrentDay: false,
          date: ''
        });
      }      
    }   

    setDays(daysArr)

  }, [events, nav])

  return (
    <div id="container">

      <CalendarHeader
        dateDisplay={dateDisplay}
        onNext={() => setNav(nav + 1)}
        onBack={() => setNav(nav - 1)}
      />

      <div id="weekdays">
        <div>Sunday</div>
        <div>Monday</div>
        <div>Tuesday</div>
        <div>Wednesday</div>
        <div>Thursday</div>
        <div>Friday</div>
        <div>Saturday</div>
      </div>

      <div id="calendar">
        {days.map((d, index) => (
        <Day
          key={index}
          day={d}
          onClick={ () => {
            if (d.value !== 'padding') {
              setClicked(d.date)
            }
          }}    
          />
          ))}
      </div>
    </div>
  )
}

export default Calendar;
